  text-decoration: none;
  color: #4a5568;
  font-weight: 500;
  font-size: 18px;
  padding: 16px 0;
  display: block;
  border-radius: 8px;
  transition: all 0.3s ease;
}

.m-menu a:hover {
  color: #007bff;
  padding-left: 20px;
}

