content.Text:
---
layout: default
title: Posts
permalink: /posts/
---

<section class="posts-page">
  <div class="inner">
    <div class="posts-hero">
      <h1 class="title">Tech Blog Posts</h1>
      <p class="subtitle">개발 여정과 기술적 인사이트를 공유합니다</p>
    </div>

    <div class="search-filter">
      <input type="text" id="search-input" placeholder="포스트 검색...">
      <div class="tag-filters" id="tag-filters">
        <!-- 태그 필터들이 JavaScript로 동적 생성됩니다 -->
      </div>
    </div>

    <div class="blog-grid" id="posts-grid">
      {% for post in site.posts %}
      <div class="blog-item" data-title="{{ post.title | downcase }}" data-tags="{% for tag in post.tags %}{{ tag | downcase }} {% endfor %}" onclick="location.href='{{ post.url | relative_url }}'">
        <div class="blog-img"></div>
        <div class="blog-content">
          <h3>{{ post.title | escape }}</h3>
          <p>{{ post.excerpt | strip_html | truncatewords: 20 }}</p>
          <div class="post-meta">
            <span class="date">{{ post.date | date: "%Y년 %m월 %d일" }}</span>
            {% if post.categories %}
            <span class="category">{{ post.categories | first }}</span>
            {% endif %}
          </div>
          {% if post.tags %}
          <div class="tags">
            {% for tag in post.tags %}
            <span class="tag">{{ tag }}</span>
            {% endfor %}
          </div>
          {% endif %}
        </div>
      </div>
      {% endfor %}
    </div>

    {% if site.posts.size == 0 %}
    <div class="no-posts">
      <h3>아직 포스트가 없습니다</h3>
      <p>첫 번째 포스트를 작성해보세요!</p>
    </div>
    {% endif %}
  </div>
</section>

<script>
// 검색 기능
const searchInput = document.getElementById('search-input');
const postsGrid = document.getElementById('posts-grid');
const blogItems = document.querySelectorAll('.blog-item');

searchInput.addEventListener('input', function() {
  const searchTerm = this.value.toLowerCase();

  blogItems.forEach(item => {
    const title = item.dataset.title;
    const tags = item.dataset.tags;

    if (title.includes(searchTerm) || tags.includes(searchTerm)) {
      item.style.display = 'block';
    } else {
      item.style.display = 'none';
    }
  });
});

// 태그 필터 생성
const allTags = new Set();
blogItems.forEach(item => {
  const tags = item.dataset.tags.trim().split(' ');
  tags.forEach(tag => {
    if (tag) allTags.add(tag);
  });
});

const tagFilters = document.getElementById('tag-filters');
allTags.forEach(tag => {
  const tagButton = document.createElement('button');
  tagButton.className = 'tag-filter';
  tagButton.textContent = tag;
  tagButton.addEventListener('click', function() {
    // 활성 상태 토글
    this.classList.toggle('active');

    // 활성 태그들 수집
    const activeTags = Array.from(document.querySelectorAll('.tag-filter.active'))
                           .map(btn => btn.textContent);

    // 필터링
    blogItems.forEach(item => {
      const itemTags = item.dataset.tags.trim().split(' ');

      if (activeTags.length === 0 || activeTags.some(tag => itemTags.includes(tag))) {
        item.style.display = 'block';
      } else {
        item.style.display = 'none';
      }
    });
  });

  tagFilters.appendChild(tagButton);
});
</script>

